mapper.update:
update BILL
   set bill_status = #{bill_status}
 where pkid = #{bill_pkid}

mapper.select:
select t2.*
  from (select t1.*, rownum row_num
          from (select t.*, count(*) over() row_total
                  from BILL t
                 where t.bill_status <> 0

                    and t.send_user_pkid in

                        #{send_user_pkid, jdbcType=VARCHAR}



                    and recv_user_pkid in

                        #{recv_user_pkid, jdbcType=VARCHAR}



                     and t.pkid in

                         #{bill_pkid, jdbcType=VARCHAR}




                     and t.bill_no like #{b_bill_no}


                     and t.bid_range = #{bid_range}


                     and t.bill_status = '1' and sysdate < t.bid_end_date


                    and (t.bill_status = '2' or t.bill_status = '3' or t.bill_status = '4'
                     or  t.bill_status = '5' or t.bill_status = '6')


                    and t.bill_status = #{bill_status}



                     and t.deliver_address like #{p_deliver_address}


                     and t.deliver_date <= (sysdate + 5)


                     and t.deliver_date > (sysdate + 5)
                     and t.deliver_date <= (sysdate + 10)


                     and t.deliver_date > (sysdate + 10)


                     and t.pd_type = #{pd_type}

                 order by t.create_date desc) t1) t2
 where t2.row_num between (#{pageIndex} - 1) * #{pageSize} + 1 and #{pageIndex} * #{pageSize}

select t2.*
from (select t1.*, rownum row_num
from (select t.*, count(*) over() row_total
from BILL t
where ((t.bid_range = '集團' and t.bid_range_value = 'all')
or (t.bid_range = '次集團' and t.bid_range_value = #{recv_user.secn_cmpy})
or (t.bid_range = '廠區' and t.bid_range_value = #{recv_user.fctry_zone})
or (t.bid_range = '事業群' and t.bid_range_value = #{recv_user.entrps_group})
or (t.bid_range = '產品處' and t.bid_range_value = #{recv_user.pd_office}))
and t.pd_type in

    #{item.name, jdbcType=VARCHAR}


    and t.bill_status = '1'
    and t.pkid in (select g.bill_pkid
    from GIVE_PRICE_MSTR g
    where g.recv_user_pkid = #{recv_user_pkid})


    and (
    t.recv_user_pkid = #{recv_user_pkid}
    and t.bill_status = #{bill_status}
    )
    or (
    t.bill_status = #{bill_status}
    and t.recv_user_pkid <> #{recv_user_pkid}
    and t.pkid in (
        select g.bill_pkid
        from GIVE_PRICE_MSTR g
        where g.recv_user_pkid = #{recv_user_pkid}
        )
    )
    or (
        t.bill_status = #{bill_status}
        and t.recv_user_pkid is null
        and t.pkid in (
            select g.bill_pkid
            from GIVE_PRICE_MSTR g
            where g.recv_user_pkid = #{recv_user_pkid}
        )
    )


    and t.recv_user_pkid = #{recv_user_pkid}
    and t.bill_status = '6'
    and t.f_recv_eval = 'N'


    and (
    (t.bill_status = '1'
    and t.pkid in (select g.bill_pkid
    from GIVE_PRICE_MSTR g
    where g.recv_user_pkid = #{recv_user_pkid}
    )
    )
    or t.recv_user_pkid = #{recv_user_pkid}
    or (
    t.bill_status <> '1'
    and t.bill_status <> '0'
    and t.recv_user_pkid <> #{recv_user_pkid}
    and t.pkid in (
    select g.bill_pkid
    from GIVE_PRICE_MSTR g
    where g.recv_user_pkid = #{recv_user_pkid}
    )
    )
    )

order by t.create_date desc, t.pd_type asc
) t1) t2
where t2.row_num between (#{pageIndex} - 1) * #{pageSize} + 1 and #{pageIndex} * #{pageSize}

select t2.* from (select t1.*, rownum row_num from (select t.*, count(*) over() row_total from BILL t where t.send_user_pkid = #{send_user_pkid}
and t.bill_status = #{bill_status}
and t.bill_status = '1' and sysdate < (t.bid_end_date)
and t.bill_status = '1' and sysdate >= (t.bid_end_date)
and t.bill_status = '6' and t.f_send_eval = 'N'
order by t.create_date desc) t1) t2 where t2.row_num between (#{pageIndex} - 1) * #{pageSize} + 1 and #{pageIndex} * #{pageSize}

select count(*)
  from BILL t
 where ((t.bid_range = '集團' and t.bid_range_value = 'all')
or (t.bid_range = '次集團' and t.bid_range_value = #{recv_user.secn_cmpy})
or (t.bid_range = '廠區' and t.bid_range_value = #{recv_user.fctry_zone})
or (t.bid_range = '事業群' and t.bid_range_value = #{recv_user.entrps_group})
or (t.bid_range = '產品處' and t.bid_range_value = #{recv_user.pd_office}))
and t.pd_type in

    #{item.name, jdbcType=VARCHAR}


    and t.bill_status = '1'
    and t.pkid in (select g.bill_pkid
    from GIVE_PRICE_MSTR g
    where g.recv_user_pkid = #{recv_user_pkid})


    and (
        t.recv_user_pkid = #{recv_user_pkid}
        and t.bill_status = #{bill_status}
    )
    or (
        t.bill_status = #{bill_status}
        and t.recv_user_pkid <> #{recv_user_pkid}
        and t.pkid in (
                        select g.bill_pkid
                        from GIVE_PRICE_MSTR g
                        where g.recv_user_pkid = #{recv_user_pkid}
                      )
    )
    or (
        t.bill_status = #{bill_status}
        and t.recv_user_pkid is null
        and t.pkid in (
        select g.bill_pkid
        from GIVE_PRICE_MSTR g
        where g.recv_user_pkid = #{recv_user_pkid}
        )
    )


    and t.recv_user_pkid = #{recv_user_pkid}
    and t.bill_status = '6'
    and t.f_recv_eval = 'N'


    and (
    (t.bill_status = '1'
    and t.pkid in (select g.bill_pkid
    from GIVE_PRICE_MSTR g
    where g.recv_user_pkid = #{recv_user_pkid}
    )
    )
    or t.recv_user_pkid = #{recv_user_pkid}
    or (
        t.bill_status <> '1'
        and t.bill_status <> '0'
        and t.recv_user_pkid <> #{recv_user_pkid}
        and t.pkid in (
                select g.bill_pkid
                from GIVE_PRICE_MSTR g
                where g.recv_user_pkid = #{recv_user_pkid}
        )
    )
    )

select t2.* from (select t1.*, rownum row_num from (select t.*, count(*) over() row_total from BILL t where ((t.bid_range = '集團' and t.bid_range_value = 'all') or (t.bid_range = '次集團' and t.bid_range_value = #{recv_user.secn_cmpy}) or (t.bid_range = '廠區' and t.bid_range_value = #{recv_user.fctry_zone}) or (t.bid_range = '事業群' and t.bid_range_value = #{recv_user.entrps_group}) or (t.bid_range = '產品處' and t.bid_range_value = #{recv_user.pd_office})) and t.pd_type in
#{item.name, jdbcType=VARCHAR}
and t.bid_range = #{bid_range}
and t.bill_status = '1' and sysdate < t.bid_end_date and t.pkid not in (select g.bill_pkid from GIVE_PRICE_MSTR g where g.recv_user_pkid = #{recv_user_pkid})
and (t.bill_status = '2' or t.bill_status = '3' or t.bill_status = '4' or t.bill_status = '5' or t.bill_status = '6')
and t.deliver_address like #{p_deliver_address}
and t.deliver_date <= (sysdate + 5)
and t.deliver_date > (sysdate + 5) and t.deliver_date <= (sysdate + 10)
and t.deliver_date > (sysdate + 10)
and t.pd_type = #{pd_type}
and t.bill_status <> '0'
order by t.create_date desc) t1) t2 where t2.row_num between (#{pageIndex} - 1) * #{pageSize} + 1 and #{pageIndex} * #{pageSize}

select count(*)
  from BILL t
 where t.send_user_pkid = #{send_user_pkid}

     and t.bill_status = #{bill_status}


     and t.bill_status = '1' and sysdate < (t.bid_end_date)


     and t.bill_status = '1' and sysdate >= (t.bid_end_date)

select t.*
  from GIVE_PRICE_MSTR t
 where t.bill_pkid = #{bill_pkid}
   and t.recv_user_pkid = #{recv_user_pkid}

select t2.* from(select t1.*,rownum row_num from(
        select t.*, count(*) over() row_total
          from RECV_EVAL t
         where t.send_user_pkid = #{send_user_pkid}
         order by t.create_date desc
         ) t1) t2
     where t2.row_num between (#{pageIndex} - 1) * #{pageSize} + 1
     and #{pageIndex} * #{pageSize}

        select t2.* from(select t1.*,rownum row_num from(
		select t.*, count(*) over() row_total
          from SEND_EVAL t
         where t.send_user_pkid = #{send_user_pkid}
         order by t.create_date desc
		 ) t1) t2
	 where t2.row_num between (#{pageIndex} - 1) * #{pageSize} + 1
	 and #{pageIndex} * #{pageSize}

select t2.* from(select t1.*,rownum row_num from(
        select t.*, count(*) over() row_total
          from SEND_EVAL t
